#!/bin/bash
# bcsample - An example of calculations with bc
if [ $# != 1 ]
  then
  echo "A number argument is required"
  exit
fi

<b>bc</b> &lt;&lt;END-OF-INPUT
  scale=6
  /* first we define the function */
  define myfunc(x){
  return(sqrt(x) + 10);
  }

  /* then use the function to do the calculation*/
 x=$1
 "Processing";x;" result is ";myfunc(x)
 quit
END-OF-INPUT

echo "(to 6 decimal places)"

echo -n "Enter the name of an animal: "
read ANIMAL
echo -n "The $ANIMAL has "
case $ANIMAL in
  (horse | dog | cat) legs=four;;
  (man | kangaroo ) legs=two;;
  (*) echo -n legs="an unknown number of";;
esac
echo $legs" legs."

